/* Plugin Database Updater 1.0.0 */
(function($){$(document).ready(function(){$('.aal_db_update a').click(function(event){event.preventDefault();var _oNotice=$(this).closest('.aal_db_update');var _oSpinner=$('<img src="'+aalDBUpdater.spinnerURL+'" alt="Spinner" />');_oSpinner.css({'vertical-align':'middle','display':'inline-block','height':'auto','margin-left':'0.5em'});_oNotice.find('p').append(_oSpinner);jQuery.ajax({type:"post",dataType:'json',url:aalDBUpdater.ajaxURL,data:{action:aalDBUpdater.actionHookSuffix,aal_nonce:aalDBUpdater.nonce,},success:function(response){if(response.success){_oNotice.removeClass('notice-info').addClass('updated');}else{_oNotice.removeClass('notice-info').addClass('error');}
_oNotice.find('p').html('<b>'+aalDBUpdater.pluginName+'</b>: '+response.result);},error:function(response){_oNotice.removeClass('notice-info').addClass('error');_oNotice.find('p').html('<b>'+aalDBUpdater.pluginName+'</b>: '+response.responseText);},complete:function(){_oSpinner.remove();}});return false;});});}(jQuery));;